sys_script_client.script:
function onLoad() {
	// definicja funkcji liczacej
	function liczKoszt(wartosc, kosztjedn){
		if (wartosc <= 0) {
			return 0;
		}
		else if (wartosc == 1){
			return kosztjedn;
		}
		else {
			return wartosc * kosztjedn + liczKoszt(wartosc-1,kosztjedn);
		}
	}

	// liczenie Cech (poziom 2 darmowy, powyzej 3*nowy poziom)
	var kosztPD = 0;
	try{
	kosztPD+= liczKoszt(g_form.getValue('u_krz'),3)-9;
	kosztPD+= liczKoszt(g_form.getValue('u_zwi'),3)-9;
	kosztPD+= liczKoszt(g_form.getValue('u_zmy'),3)-9;
	kosztPD+= liczKoszt(g_form.getValue('u_mdr'),3)-9;
	kosztPD+= liczKoszt(g_form.getValue('u_leb'),3)-9;
	kosztPD+= liczKoszt(g_form.getValue('u_fan'),3)-9;		
	} catch(err){
		jslog("Błąd w liczeniu kosztu Cech: "+err.message);
	}

	// liczenie BB (2*nowy poziom)
	try{
	kosztPD+= liczKoszt(g_form.getValue('u_bbw1'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw2'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw3'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw4'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw5'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw6'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw7'),2);
	kosztPD+= liczKoszt(g_form.getValue('u_bbw8'),2);
	} catch(err) {
		jslog("Błąd w liczeniu kosztu BB: "+err.message);
	}

	// liczenie BZ (1*nowy poziom)
	try{
	kosztPD+= liczKoszt(g_form.getValue('u_bzw1'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw2'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw3'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw4'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw5'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw6'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw7'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw8'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw9'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw10'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw11'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw12'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw13'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw14'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw15'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw16'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw17'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw18'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw19'),1);
	kosztPD+= liczKoszt(g_form.getValue('u_bzw20'),1);
	} catch(err) {
		jslog("Błąd w liczeniu kosztu BZ: "+err.message);
	}	

	// zapis do pola
	g_form.setValue('u_koszt',kosztPD);
}
